import React, {
  useImperativeHandle,
  forwardRef,
  useEffect,
  useState,
} from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Dummydata from "../../utils/supportDummyData/openListingDummy.json";
import Menu from "../custom/support/Menu";
import Modal from "react-modal";
import "../../styles/support/ListingDetails.css";
import { EnvironmentOutlined, GlobalOutlined } from "@ant-design/icons";
import CustomFeatureField from "../custom/support/custom.FeatureField";
import SupportNavigation from "../custom/support/custom.NavigationComponent";
import FooterComponent from "../layout/support/FooterComponent";
import CustomSelectTypeField from "../custom/support/custom.SelectTypeField";
import CustomTextField from "../custom/support/custom.TextField";
import PhotoGallery from "../custom/support/PhotoGallery";
import PropertyMapButton from "../custom/support/PropertyMapModal";
import ApprovalComponent from "../custom/support/ApprovalComponent";
import TitleComponent from "../custom/support/TitleComponent";

Modal.setAppElement("#root");

const ListingDetails = forwardRef((props, ref) => {
  const { labelname, classname } = props;
  const [numFieldsIndoor, setNumFieldsIndoor] = useState(4);
  const [numFieldsOutdoor, setNumFieldsOutdoor] = useState(4);
  const [numFieldsAmmenities, setNumFieldsAmmenities] = useState(2);
  const [numFieldsIncludes, setNumFieldsIncludes] = useState(2);
  const propertyAddress = "123 Property St, City, Country";
  const [isEditMode, setIsEditMode] = useState(false);
  const [isFirstModalOpen, setIsFirstModalOpen] = useState(false);
  const [isSecondModalOpen, setIsSecondModalOpen] = useState(false);
  const openFirstModal = () => setIsFirstModalOpen(true);
  const closeFirstModal = () => setIsFirstModalOpen(false);
  const openSecondModal = () => {
    closeFirstModal();
    setIsSecondModalOpen(true);
  };
  const handleCancelEdit = () => {
    setIsEditMode(false);
  };

  const closeSecondModal = () => {
    setIsSecondModalOpen(false);
    setIsEditMode(false);
  };
  const [selectedValuesIndoor, setSelectedValuesIndoor] = useState(
    new Array(4).fill("")
  );
  const [selectedValuesOutdoor, setSelectedValuesOutdoor] = useState(
    new Array(4).fill("")
  );
  const [selectedValuesAmmenities, setSelectedValuesAmmenities] = useState(
    new Array(2).fill("")
  );
  const [selectedValuesIncludes, setSelectedValuesIncludes] = useState(
    new Array(2).fill("")
  );

  const [errors, setErrors] = useState({
    indoor: false,
    outdoor: false,
    ammenities: false,
    includes: false,
  });

  useImperativeHandle(ref, () => ({
    validateFields,
  }));

  const handleAddField = (type, setNumFields, numFields, setValues) => {
    setNumFields(numFields + 1);
    setValues((prev) => [...prev, ""]);
  };

  const handleRemoveField = (type, setNumFields, numFields, minFields) => {
    if (numFields > minFields) {
      setNumFields(numFields - 1);
    }
  };

  const handleSelectChange = (index, value, setValues) => {
    setValues((prevValues) => {
      prevValues[index] = value;
      return [...prevValues];
    });
  };

  const validateFields = () => {
    const newErrors = {
      indoor: selectedValuesIndoor.some((value) => value === ""),
      outdoor: selectedValuesOutdoor.some((value) => value === ""),
      ammenities: selectedValuesAmmenities.some((value) => value === ""),
      includes: selectedValuesIncludes.some((value) => value === ""),
    };

    setErrors(newErrors);

    return !Object.values(newErrors).some((error) => error);
  };
  const navigate = useNavigate();
  const location = useLocation();
  const { listing, activeTab } = location.state || {};

  const [subdivision, setSubdivision] = useState("");

  const [detailedListing, setDetailedListing] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [remarks, setRemarks] = useState("");
  const [actionType, setActionType] = useState("");
  const [label, setLabel] = useState("");

  useEffect(() => {
    if (listing) {
      const listings = Dummydata[`${activeTab}_listings`] || [];
      const detailed = listings.find(
        (item) => item.listing_id === listing.listing_id
      );
      if (detailed) {
        setDetailedListing(detailed);
      } else {
        console.error(
          `Detailed listing not found for listing ID ${listing.listing_id}`
        );
      }
    }
  }, [listing, activeTab]);

  const tabHeadings = {
    pending: "Pending Listings",
    disapproved: "Denied Listings",
    approved: "Approved Listings",
    active: "Active Listings",
  };

  const handleClose = () => {
    setShowModal(false);
    setRemarks("");
  };

  if (!detailedListing) {
    return <div>Loading...</div>;
  }

  const navLinks = [
    {
      text: "Create listing",
      to: "/ML-Brokerage/Support/SupportCreateListingPage",
    },
    {
      text: "Listing Masterlist",
      dropdown: true,
      options: [
        { text: "Pending Listings", to: "/ML-Brokerage/Support/pending" },
        { text: "Active Listings", to: "/ML-Brokerage/Support/active" },
        {
          text: "Denied Listings",
          to: "/ML-Brokerage/Support/disapproved",
        },
      ],
    },
    {
      text: "Application Review",
      dropdown: true,
      options: [
        {
          text: "Pending Applications",
          to: "/ML-Brokerage/Support/pendingApplication",
        },
        {
          text: "Approved Applications",
          to: "/ML-Brokerage/Support/openApplication",
        },
        {
          text: "Denied Applications",
          to: "/dashboard/Support/disapprovedApplication",
        },
        {
          text: "Canceled Applications",
          to: "/dashboard/Support/CanceledApplications",
        },
        {
          text: "Closed Applications ",
          to: "/dashboard/Support/ClosedApplications",
        },
      ],
    },
    {
      text: "Pre-Approved Request",
      to: "/ML-Brokerage/Support/pre-approved",
    },
    { text: "Client Management", to: "/ML-Brokerage/Support/SupportDashboard" },
  ];
  const photos = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
  ];
  const tabDetails = {
    disapproved: { title: "Denied Listings", color: "Red" },
    active: { title: null, color: null },
    pending: { title: null, color: null },
  };
  return (
    <div className="listing-details-container">
      <SupportNavigation navLinkProps={navLinks} />
      <div className="bodySection">
        <div className="secondTab">
          <Menu activeTab={activeTab} tabHeadings={tabHeadings} />
          <p>
            <strong>Property ID:</strong> {detailedListing.listing_id}
          </p>
        </div>
        <hr style={{ border: "#D90000 solid 1px", width: "100%" }} />
        <div className="contentContainer">
          <center>
            {" "}
            <TitleComponent
              title={tabDetails[activeTab].title}
              color={tabDetails[activeTab].color}
            />
          </center>
          <div className="btns">
            {!isEditMode &&
              !(activeTab === "disapproved") &&
              !(activeTab === "active") && (
                <a
                  href="#"
                  className="sbutton"
                  onClick={() => setIsEditMode(true)}
                >
                  <p>Edit Listing</p>
                </a>
              )}
            <PropertyMapButton address={propertyAddress} />

            <a href="#" className="sbutton">
              <GlobalOutlined />
              <p>View Listing as Public </p>
            </a>
          </div>

          <div className="fields">
            <div className="leftSide">
              <div className="propertyDetailsText">Property Details</div>
              <div className="propertyDetailsFields">
                <CustomSelectTypeField
                  disabled={!isEditMode}
                  labelName="Property Type"
                />
                <CustomSelectTypeField
                  disabled={!isEditMode}
                  labelName="Listing Type"
                />
              </div>
              <div className="unitDetailsText">Unit Details</div>
              <div className="unitDetailsFields">
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Selling Price"
                />
                <CustomTextField
                  inputType="input"
                  labelName="Discounted Selling Price"
                  disabled={!isEditMode}
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Furnishing"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Classification"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Beds"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Bathrooms"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Parking"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="No of Floors"
                />
                <CustomTextField
                  inputType="input"
                  labelName="Floor Area (sqm)"
                  disabled={!isEditMode}
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Lot Area (sqm)"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Price per sqm"
                />
                <CustomTextField
                  inputType="input"
                  labelName="Property ID"
                  value={detailedListing.listing_id}
                  disabled={!isEditMode}
                />
              </div>
              <div className="location">
                <div className="locationText">Location</div>
                <CustomTextField
                  inputType="textarea"
                  labelName="Subdivision"
                  value={subdivision}
                  onChange={(e) => setSubdivision(e.target.value)}
                  disabled={!isEditMode}
                />
                <CustomTextField
                  inputType="textarea"
                  labelName="Address"
                  value={subdivision}
                  onChange={(e) => setSubdivision(e.target.value)}
                  disabled={!isEditMode}
                />
                <CustomTextField
                  inputType="input"
                  labelName="Map Location"
                  value={subdivision}
                  onChange={(e) => setSubdivision(e.target.value)}
                  disabled={!isEditMode}
                />
              </div>
            </div>
            <div className="rightSide">
              <div className="descriptionText">Description</div>
              <div className="descriptionFields">
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="input"
                  labelName="Title"
                />
                <CustomTextField
                  disabled={!isEditMode}
                  inputType="textarea"
                  labelName="Caption"
                />
              </div>
            </div>
          </div>
        </div>
        <div className="features">
          <div className="featureText">Features</div>

          {/* Indoor Features */}
          <div className="IndoorFeatures">
            <div className="indoorFeaturesText">Indoor Features</div>
            <div className="featuresButton">
              <button
                onClick={() =>
                  handleAddField(
                    "indoor",
                    setNumFieldsIndoor,
                    numFieldsIndoor,
                    setSelectedValuesIndoor
                  )
                }
                className="addButton"
                disabled={!isEditMode}
              >
                Add +
              </button>
              <button
                onClick={() =>
                  handleRemoveField(
                    "indoor",
                    setNumFieldsIndoor,
                    numFieldsIndoor,
                    4
                  )
                }
                disabled={numFieldsIndoor <= 4 || !isEditMode}
                className="subtractButton"
              >
                Remove
              </button>
            </div>
          </div>
          <div className="inputFieldss">
            {[...Array(numFieldsIndoor)].map((_, index) => (
              <div key={index}>
                <CustomFeatureField
                  labelName="Indoor Features"
                  onChange={(value) =>
                    handleSelectChange(index, value, setSelectedValuesIndoor)
                  }
                  value={selectedValuesIndoor[index]}
                  fieldType="select"
                  className="fieldFeatures"
                  disabled={!isEditMode}
                />
                {errors.indoor && selectedValuesIndoor[index] === "" && (
                  <div className="error">This field is required</div>
                )}
              </div>
            ))}
          </div>

          {/* Outdoor Features */}
          <div className="OutdoorFeatures">
            <div className="outdoorFeaturesText">Outdoor Features</div>
            <div className="featuresButton">
              <button
                onClick={() =>
                  handleAddField(
                    "outdoor",
                    setNumFieldsOutdoor,
                    numFieldsOutdoor,
                    setSelectedValuesOutdoor
                  )
                }
                className="addButton"
                disabled={!isEditMode}
              >
                Add +
              </button>
              <button
                onClick={() =>
                  handleRemoveField(
                    "outdoor",
                    setNumFieldsOutdoor,
                    numFieldsOutdoor,
                    4
                  )
                }
                disabled={numFieldsOutdoor <= 4 || !isEditMode}
                className="subtractButton"
              >
                Remove
              </button>
            </div>
          </div>
          <div className="inputFieldss">
            {[...Array(numFieldsOutdoor)].map((_, index) => (
              <div key={index}>
                <CustomFeatureField
                  labelName="Outdoor Features"
                  onChange={(value) =>
                    handleSelectChange(index, value, setSelectedValuesOutdoor)
                  }
                  value={selectedValuesOutdoor[index]}
                  fieldType="select"
                  className="fieldFeatures"
                  disabled={!isEditMode}
                />
                {errors.outdoor && selectedValuesOutdoor[index] === "" && (
                  <div className="error">This field is required</div>
                )}
              </div>
            ))}
          </div>

          {/* Features and Ammenities */}
          <div className="featuresAndAmmenities">
            <div className="featuresAndAmenitiesText">
              Features and Amenities
            </div>
            <div className="featuresButton">
              <button
                onClick={() =>
                  handleAddField(
                    "ammenities",
                    setNumFieldsAmmenities,
                    numFieldsAmmenities,
                    setSelectedValuesAmmenities
                  )
                }
                className="addButton"
                disabled={!isEditMode}
              >
                Add +
              </button>
              <button
                onClick={() =>
                  handleRemoveField(
                    "ammenities",
                    setNumFieldsAmmenities,
                    numFieldsAmmenities,
                    2
                  )
                }
                disabled={numFieldsAmmenities <= 2 || !isEditMode}
                className="subtractButton"
              >
                Remove
              </button>
            </div>
          </div>
          <div className="ammenitiesFields">
            {[...Array(numFieldsAmmenities)].map((_, index) => (
              <div key={index}>
                <CustomFeatureField
                  labelName="Ammenities Features"
                  onChange={(value) =>
                    handleSelectChange(
                      index,
                      value,
                      setSelectedValuesAmmenities
                    )
                  }
                  value={selectedValuesAmmenities[index]}
                  fieldType="text"
                  className="ammenitiesInputField"
                  disabled={!isEditMode}
                />
                {errors.ammenities &&
                  selectedValuesAmmenities[index] === "" && (
                    <div className="error">This field is required</div>
                  )}
              </div>
            ))}
          </div>

          {/* Includes */}
          <div className="includes">
            <div className="includesText">Includes</div>
            <div className="featuresButton">
              <button
                onClick={() =>
                  handleAddField(
                    "includes",
                    setNumFieldsIncludes,
                    numFieldsIncludes,
                    setSelectedValuesIncludes
                  )
                }
                className="addButton"
                disabled={!isEditMode}
              >
                Add +
              </button>
              <button
                onClick={() =>
                  handleRemoveField(
                    "includes",
                    setNumFieldsIncludes,
                    numFieldsIncludes,
                    2
                  )
                }
                disabled={numFieldsIncludes <= 2 || !isEditMode}
                className="subtractButton"
              >
                Remove
              </button>
            </div>
          </div>
          <div className="includesFields">
            {[...Array(numFieldsIncludes)].map((_, index) => (
              <div key={index}>
                <CustomFeatureField
                  labelName="Includes Features"
                  onChange={(value) =>
                    handleSelectChange(index, value, setSelectedValuesIncludes)
                  }
                  value={selectedValuesIncludes[index]}
                  fieldType="text"
                  className="includesInputField"
                  disabled={!isEditMode}
                />
                {errors.includes && selectedValuesIncludes[index] === "" && (
                  <div className="error">This field is required</div>
                )}
              </div>
            ))}
          </div>
          <b>Uploaded Photos</b>
          <PhotoGallery disabled={!isEditMode} photos={photos} />
          {isEditMode && (
            <div className="updatesButton">
              <button className="updateBtn" onClick={openFirstModal}>
                Update Details
              </button>
              <button className="cancelBtn" onClick={handleCancelEdit}>
                Cancel
              </button>
            </div>
          )}
        </div>
        <Modal
          isOpen={isFirstModalOpen}
          onRequestClose={closeFirstModal}
          className="fmodal"
          overlayClassName="overlay"
          contentLabel="First Modal"
        >
          <h2>Confirmation Message</h2>
          <p>Are you sure you want to save changes?</p>
          <div>
            <button className="button button-primary" onClick={openSecondModal}>
              Save
            </button>
            <button
              className="button button-secondary"
              onClick={closeFirstModal}
            >
              Cancel
            </button>
          </div>
        </Modal>

        <Modal
          isOpen={isSecondModalOpen}
          onClick={closeSecondModal}
          className="smodal"
          overlayClassName="overlay"
          contentLabel="Second Modal"
        >
          <h2>Successful Message</h2>
          <p>Listing details has successfully updated! </p>
          <button onClick={closeSecondModal} className="button button-primary">
            Close
          </button>
        </Modal>
        <ApprovalComponent activeTab={activeTab} />
        <FooterComponent />
      </div>
    </div>
  );
});

export default ListingDetails;
